namespace ProjectDelta.Tools.PreStartChecks
{
    internal class DLLHash
    {
        public static bool ServerCertificateValidationCallback(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)
        {
            if (certificate.GetRawCertDataString() == "") return true; else return false;
        }

        public static string GetHashes()
        {
            try
            {
                string url = B64X.Encrypt("http://a116901.hostde27.fornex.host/delta/license/hashes.txt");
                HttpWebRequest requestnewversion = (HttpWebRequest)WebRequest.Create(B64X.Decrypt(url));
                requestnewversion.Method = "GET";
                requestnewversion.ServerCertificateValidationCallback += ServerCertificateValidationCallback;
                HttpWebResponse responze = (HttpWebResponse)requestnewversion.GetResponse();
                string html = B64X.Encrypt(new StreamReader(responze.GetResponseStream()).ReadToEnd());
                return html;
            }
            catch { return B64X.Encrypt("error"); }
        }

        public static string CalculateMD5(string filename)
        {
            try
            {
                using (var md5 = MD5.Create())
                {
                    using (var stream = File.OpenRead(filename))
                    {
                        var hash = md5.ComputeHash(stream);
                        return BitConverter.ToString(hash).Replace("-", "").ToLowerInvariant();
                    }
                }
            }
            catch { return "error"; }
        }

        public static void CheckDLLHash()
        {
            /*File.AppendAllText("hashes.txt", CalculateMD5("BouncyCastle.Crypto.dll") + Environment.NewLine);
            File.AppendAllText("hashes.txt", CalculateMD5("Newtonsoft.Json.dll") + Environment.NewLine);
            File.AppendAllText("hashes.txt", CalculateMD5("xNet.dll") + Environment.NewLine);
            //File.AppendAllText("hashes.txt", CalculateMD5("TranslationProperties.dll") + Environment.NewLine);
            Environment.Exit(0);*/

            bool q = false;
            string hashstr = B64X.Decrypt(GetHashes());
            if (hashstr == "error") q = true;
            if (!q)
            {
                var hashes = B64X.Encrypt(hashstr.Split(new char[2] { '\r', '\n' }, StringSplitOptions.RemoveEmptyEntries));
                if (CalculateMD5("BouncyCastle.Crypto.dll") != B64X.Decrypt(hashes[0])) q = true;
                if (CalculateMD5("Newtonsoft.Json.dll") != B64X.Decrypt(hashes[1])) q = true;
                if (CalculateMD5("xNet.dll") != B64X.Decrypt(hashes[2])) q = true;
            }
            if (q)
            {
                if (!Program.silent) MessageBox.Show("Something went wrong!", "Error!", MessageBoxButtons.OK, MessageBoxIcon.Error);
                Environment.Exit(0);
            }
        }
    }
